<?phpob_start();session_start();include("includes/config.php");$email = $_POST["email"];$senha = md5($_POST["senha"]);$resUser = $mysqli->query("SELECT * FROM usuario WHERE UsuEmail = '$email' AND UsuSenha = '$senha'") or die($mysqli->error);if($resUser->num_rows == 1){	$user = $resUser->fetch_object();	$_SESSION['IdUsuario'] = $user->IdUsuario;	$_SESSION['UsuNome']	 = $user->UsuNome;	$_SESSION['UsuEmail']  = $user->UsuEmail;	$_SESSION['UsuImagem'] = $user->UsuImagem;	$_SESSION['UsuNivel'] = $user->UsuNivel;	if($user->UsuNivel == 1){		$dados = 1;	}else{		$dados = 2;	}		echo json_encode($dados);}?>